CREATE TABLE `ems`.`employee` (`emp_id` INT NOT NULL AUTO_INCREMENT , `username` VARCHAR NOT NULL , `email` VARCHAR NOT NULL , `password` VARCHAR NOT NULL , `full_name` VARCHAR NOT NULL , `gender` VARCHAR NOT NULL , `created_at` TIMESTAMP NOT NULL DEFAULT CURRENT_TIMESTAMP , `updated_at` TIMESTAMP on update CURRENT_TIMESTAMP NOT NULL , UNIQUE (`username`), UNIQUE (`email`)) ENGINE = InnoDB;